import React, { useState, useEffect } from 'react';
import './WebForm.css';
import './Confirmation.css';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';

function WebForm() {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    numberOfPeople: '',
    date: new Date(),
    time: '',
    comments: ''
  });

  const [reservedSlots, setReservedSlots] = useState([]);

  useEffect(() => {
    fetch('http://127.0.0.1:5000/view-submission')
        .then(response => response.json())
        .then(data => {
            // Assuming each item in 'data' contains 'date' and 'time'
            const formattedData = data.map(item => ({
                date: item.date,
                time: item.time
            }));
            setReservedSlots(formattedData);
        })
        .catch(error => console.error('Error fetching available slots:', error));
  }, []);

  const ResetForm = () => {
    setFormData({
      firstName: '',
      lastName: '',
      email: '',
      numberOfPeople: '',
      date: new Date(),
      time: '',
      comments: ''
    });
    setIsSubmitted(false);
  };

  const [isSubmitted, setIsSubmitted] = useState(false);
  const [submittedData, Submission] = useState({});

  const handleChange = (event) => {
    setFormData({
      ...formData,
      [event.target.name]: event.target.value
    });
  };

  const handleDateChange = (date) => {
    setFormData({ ...formData, date });
  };

  const isDateReserved = (date) => {
    // Format the date object to match the reservedSlots date string format
    const dateString = date.toISOString().substring(0, 9); // Converts to 'YYYY-MM-DD' format

    // Check if the formatted date string is in any of the date entries in reservedSlots
    return reservedSlots.some(slot => slot.date === dateString);
  };

  const notAvailableDate = (date) => {
    if (date.getDay() === 0) {
      return true;
    }

    return isDateReserved(date);
  };

  // Generate time options from 8 AM to 7 PM
  const generateTimeOptions = () => {
    const options = [];
    for (let hour = 8; hour <= 19; hour++) {
      const time = hour < 10 ? `0${hour}:00` : `${hour}:00`;
      options.push(<option key={time} value={time}>{time}</option>);
    }
    return options;
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await fetch('http://127.0.0.1:5000/submit-form', { // Replace with your backend URL
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),

      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const responseData = await response.json();
      console.log(responseData); //  handle the response here
    } catch (error) {
      console.error('There was an error submitting the form:', error);
    }

    Submission(formData);
    setIsSubmitted(true); // Set the form as submitted

  };


  if (isSubmitted) {
    return (
      <div className="submission-details">
        <h3>Reservation Confirmed!</h3>
        <p>Thank you, {submittedData.firstName} {submittedData.lastName}, for your reservation.</p>
        <p>Email: {submittedData.email}</p>
        <p>Number of People: {submittedData.numberOfPeople}</p>
        <p>Date: {submittedData.date.toString()}</p>
        <p>Time: {submittedData.time}</p>
        <p>Comments: {submittedData.comments}</p>
        <button className="submit-button" onClick={ResetForm}>Make Another Reservation</button>
      </div>
    );
  }

  return (
    <form onSubmit={handleSubmit} className="reservation-form">
      <div className="form-row">
        <div className="form-group">
          <label>First Name</label>
          <input type="text" name="firstName" value={formData.firstName} onChange={handleChange} />
        </div>
        <div className="form-group">
          <label>Last Name</label>
          <input type="text" name="lastName" value={formData.lastName} onChange={handleChange} />
        </div>
      </div>

      <div className="form-group">
        <label>Email Address</label>
        <input type="email" name="email" value={formData.email} onChange={handleChange} />
      </div>

      <div className="form-group">
        <label>Number of People</label>
        <input type="number" name="numberOfPeople" value={formData.numberOfPeople} onChange={handleChange} />
      </div>

      <div className="form-group">
        <label>Date of Reservation</label>
        <DatePicker
          selected={formData.date}
          onChange={handleDateChange}
          filterDate={date => !notAvailableDate(date)}
          minDate={new Date()}
          dateFormat="yyyy-MM-dd"
        />
      </div>

      <div className="form-group">
        <label>Time of Reservation (8 AM to 7 PM)</label>
        <select name="time" value={formData.time} onChange={handleChange} required>
          <option value="">Select a time</option>
          {generateTimeOptions()}
        </select>
      </div>

      <div className="form-group">
        <label>Additional Comments</label>
        <textarea name="comments" value={formData.comments} onChange={handleChange} rows="4"></textarea>
      </div>

      <button type="submit" className="submit-button">Submit Reservation</button>
    </form>
  );

}

export default WebForm;
